import logo from "./logo.svg";
import "./App.css";
import { useState } from "react";
import Form from './form'
import React from "react";
function App() {
  const [name, setName] = useState("");
 // const [email, setEmail] = useState("");
  const [age, setAge] = useState<number>(0); //ここをletにした


  const onSubmit = async (e: React.FormEvent<HTMLFormElement>,name:string,age:number) => {
    e.preventDefault();


    console.log("onSubmit: ", name, " ", age);


    if (!name) {
      alert("Please enter name");
      return;
    }

    if (name.length > 50) {
      alert("Please enter a name shorter than 50 characters");
      return;
    }

    if (age < 20 || age > 80) {
      alert("Please enter age between 20 and 80");
      return;
    }

    try {
      const result = await fetch("http://localhost:8000/user", {
        method: "POST",
        body: JSON.stringify({
          name: name,
          age: age,
        }),

      });

     const response = await fetch(
        "http://localhost:8000/user",
        {
          method: "GET",
          headers: {
            "Content-Type": "application/json",
          },
        }
      );
      console.log("response is...", await response.json());  


      if (!result.ok) {
        throw Error(`Failed to create user: ${result.status}`);
      }

      setName("");
      setAge(0);
      //todo Goのサーバーからすべてのuserをgetしuser一覧を更新する
      //fetchUsers();
    } catch (err) {
       console.error(err);
    }
  }; 






  return (
    <div className="App">
      <header className="App-header">
        <div className= "header-slogan"></div>
        <h1>User Register</h1>

      </header>
        <Form onSubmit={onSubmit}/>



    </div>
  );
}

export default App;
