  const [name, setName] = useState("");
 // const [email, setEmail] = useState("");
  const [age, setAge] = useState<number>(0); //ここをletにした
  const [users, setUsers] = useState([]);//これを加えた

  const onSubmit = async (e: React.FormEvent<HTMLFormElement>,name:string,age:number) => {
    e.preventDefault();


    console.log("onSubmit: ", name, " ", age);


    if (!name) {
      alert("Please enter name");
      return;
    }

    if (name.length > 50) {
      alert("Please enter a name shorter than 50 characters");
      return;
    }

    if (age < 20 || age > 80) {
      alert("Please enter age between 20 and 80");
      return;
    }


    const fetchUsers = async () => {


    try {
      const result = await fetch("http://localhost:8000/user", {
        method: "POST",
        body: JSON.stringify({
          name: name,
          age: age,
        }),

      });


     const response = await fetch(
        "http://localhost:8000/user",
        {
          method: "GET",
          headers: {
            "Content-Type": "application/json",
          },
        }
      );

      /*useEffect(() => {
        // 処理
        fetchUsers()
      }, [value]);
      */

      if (!result.ok) {
        throw Error(`Failed to create user: ${result.status}`);
      }




      fetchUsers();
      setName("");
      setAge(0);

      //todo Goのサーバーからすべてのuserをgetしuser一覧を更新する
      //fetchUsers();
    } catch (err) {
       console.error(err);
    }
  }; //fetchusers
}; //onsubmit

useEffect(() => {
  //todo これは起動時になるように揃える
  fetchUsers()
}, [])

const fetchUsers = async () => {
  try {
    const res = await fetch("http://localhost:8000/users");
    if (!res.ok) {
      throw Error(`Failed to fetch users: ${res.status}`);
    }

    const users = await res.json();
    setUsers(users);
  } catch (err) {
    console.error(err);
  }
};









  return (
    <div className="App">
      <header className="App-header">
        <div className= "header-slogan"></div>
        <h1>User Register</h1>

      </header>
        <Form onSubmit={onSubmit}/>

      <div>
      <h2>ユーザ一覧</h2>
      </div>


    </div>
  );
}

export default App;
